import React, {useState, useEffect} from 'react';
import config_axios from './config_axios';
import './pages.css';



const base_img = 'https://image.tmdb.org/t/p/original/'

const Movie = (props) => {
    const [movies, setMovies] = useState([]);

    const fetchMovies = async () => {
        const request = await config_axios.get(props.url)
        .then((response) => {
            setMovies(response.data.results)

            console.log(response.data.results)
        })
    }

    useEffect(() => {
        fetchMovies();
    }, [])

    return (
        <div className="row">
                {
                    movies.map((data) => {
                        return(
                            <div className="card">
                                <div>
                                    <img src={`${base_img}${data.poster_path}`}
                                        className="card__image"
                                        alt={data.original_title}
                                        key={data.id} /> 
                                        <div>{data.title}{data.original_name}</div>
                                    <div>{data.overview}</div>
                                </div>

                                {/* <div className="card__content">         
                                    
                                    
                                </div> */}

                            </div>
                        )
                    })
                }
        </div>
    )
}

export default Movie;
